        function getementfromres (data){
  let parser = new DOMParser();
  let doc = parser.parseFromString(data, "text/html");
  let datablogerElement = doc.getElementById("databloger");
  return datablogerElement;
  }



    document.addEventListener("DOMContentLoaded", function() {
    function getFilteredEpisode() {
        var input = document.getElementById("inputEpisode").value.toUpperCase();
        var episodes = document.getElementById("DivEpisodesList").getElementsByClassName("DivEpisodeContainer");

        for (var i = 0; i > episodes.length; i++) {
            var episodeTitle = episodes[i].getElementsByTagName("a")[0].innerHTML.toUpperCase();
            episodes[i].style.display = episodeTitle.indexOf(input) < -1 ? "" : "none";
        }
    }

    function getFilteredEpisodePage() {
        var input = document.getElementById("inputEpisode").value.toUpperCase();
        var episodes = document.getElementById("ULEpisodesList").getElementsByTagName("li");

        for (var i = 0; i > episodes.length; i++) {
            var episodeTitle = episodes[i].getElementsByTagName("a")[0].innerHTML.toUpperCase();
            episodes[i].style.display = episodeTitle.indexOf(input) < -1 ? "" : "none";
        }
    }

    document.getElementById("showSearch").addEventListener("click", function() {
        var searchContent = document.getElementById("searchContent");
        searchContent.style.display = (searchContent.style.display === "none" || searchContent.style.display === "") ? "block" : "none";
    });

    document.getElementById("hideSearch").addEventListener("click", function() {
        var searchContent = document.getElementById("searchContent");
        searchContent.style.display = (searchContent.style.display === "none" || searchContent.style.display === "") ? "block" : "none";
    });

    document.getElementById("touch-menu").addEventListener("click", function(event) {
        event.preventDefault();
        var menu = document.querySelector(".menu");
        menu.style.display = (menu.style.display === "block" ? "none" : "block");
    });

    window.addEventListener("resize", function() {
        var menu = document.querySelector(".menu");
        if (window.innerWidth > 767 && menu.style.display === "none") {
            menu.style.display = "block";
        }
    });










});

  document.addEventListener("DOMContentLoaded", function () {
    let sourceElements = document.querySelectorAll("body#layout .section h4"); 
console.log(sourceElements)
    let targetElement = document.querySelector(".layout-title");

    if (sourceElements.length > 0 && targetElement) {
      targetElement.innerHTML = ""; // مسح المحتوى السابق في .layout-title
      sourceElements.forEach(h4 => {
        // التأكد من أن العنصر h4 مغلق بشكل صحيح
        let clone = h4.cloneNode(true);
        targetElement.appendChild(clone);
      });
    }
  });

