              function labels2(data) {
  const labels = data || [];
    let typeHTML = "";

    if (labels.some(cat => cat.term == "يعرض-الان")) {
        typeHTML = `


                              يعرض الان

    `;
    } else if (labels.some(cat => cat.term == "Movie")) {
        typeHTML = `


                              مكتمل

    `;
    } else if (labels.some(cat => cat.term == "لم-يعرض-بعد")) {
        typeHTML = `


                              لم يعرض بعد

    `;
    } 



  return typeHTML
      } 



              function labels(data) {
  const labels = data || [];
    let typeHTML = "";

    if (labels.some(cat => cat.term == "tv")) {
        typeHTML = `


     TV

    `;
    } else if (labels.some(cat => cat.term == "movie")) {
        typeHTML = `



     Movie


    `;
    } else if (labels.some(cat => cat.term == "ona")) {
        typeHTML = `


     ONA


    `;
    } else if (labels.some(cat => cat.term == "ova")) {
        typeHTML = `


     OVA


    `;
    } else if (labels.some(cat => cat.term == "special")) {
        typeHTML = `


     Special


    `;
    }



  return typeHTML
      }







