         // ------------------------ search in episode ---------------------------------


            function search(query) {

    // تحويل قيمة الإدخال إلى رقم
    let searchNumber = parseInt(query.trim());


   document.querySelectorAll("#DivEpisodesList > div").forEach((episode) => {
            let episodeNumberElement = episode.querySelector("a"); // الحصول على النص داخل العنصر

            if (episodeNumberElement) {
                let episodeNumber = episodeNumberElement.textContent.replace("الحلقة ", "").trim();

                // إذا كان الإدخال فارغًا، أظهر جميع الحلقات
                if (query === "") {
                    episode.classList.remove("hidden");
                } else {
                    // قارن رقم الحلقة وأعرض فقط المتطابق
                    if (episodeNumber == query) {
                        episode.classList.remove("hidden");
                    } else {
                        episode.classList.add("hidden");
                    }
                }
            }
        });
}

